INSERT INTO `vnt_services` VALUES (188, 'Khu nghỉ dưỡng Salinda Premium Phú Quốc 5 sao duy nhất trên đảo ngọc mới đi vào hoạt đông đầu năm 2014, nằm cách sân bay Đông Dương khoảng 4km. Salinda Preminum Resort & Spa Phú Quốc mang trong mình nét kiến trúc độc đáo nhất từ trước đến nay, hẳn là sẽ không phải là sự kết hợp hài hòa giữa thiên nhiên nữa, mà đó chính là thiên nhiên. Trong không gian thơ mộng trên đảo, du khách hòa mình vào giữa thiên nhiên cùng với làn gió, dòng biển, những thảm cỏ xanh.\r\nKhu nghỉ dưỡng Salinda Premium mang hơi thở đậm chất Á Đông, tinh tế bởi nét truyền thống và hiện đại, giữa văn hóa và kiến trúc độc đáo. Điều này được thể hiện qua không gian phòng nghỉ, kiến trúc nhà hàng hay từ những dịch vụ mang mà resort cung cấp. Nhà hàng Salinda chuyên phục vụ các món Ý, Âu  và Á với một không gian sang trọng, tại đây du khách có thể nhìn thấy trực tiếp những đầu bếp trong quy trình chế biến món ăn của họ. Đặc biệt là các món Ý, du khách sẽ được yêu cầu trong thực đơn pizza theo phong cách riêng của mình và tất nhiên là có sự hỗ trợ của các chuyên gia ẩm thực. Salinda pool bar là không gian cho du khách nhâm nhi một ly cocktail và hòa mình vào làn nước biển trong hồ bơi của resort. Dịch vụ Spa với liệu pháp mang thương hiệu Guru Thái Lan mang lại nguồn năng lượng dồi dào cho du khách sau những ngày dài hoạt động khám phá, chăm sóc sức khỏe và làm đẹp từ đôi bàn tay của các chuyên gia massage có kinh nghiệm cao. Với sức chứa 500 khách là không gian tiệc và tổ chức sự kiện của Salinda Premium resort & Spa Phú Quốc, là nơi lý tưởng cho các đôi bạn trẻ tổ chức ngày đáng nhớ nhất trong đời mình.', '', '05:00', '18:00', '0', '0', 'salindaresort.com', '+84 8 3929 3', 0, 2345, '1', 1, 180, NULL, NULL, NULL, '2018-03-30 20:00:00', '2018-03-30 20:00:00');
INSERT INTO `vnt_services` VALUES (189, 'Khu nghỉ dưỡng Peppercorn Beach Resort Phú Quốc là khu nghỉ dưỡng 3 sao, tọa lạc tại Gành Dầu một thắng cảnh nổi tiếng trên đảo. Nằm cách sân bay Dương Đông khoảng 15km và chỉ mất 35 phút bay từ thành phố Hồ Chí Minh. Khu nghỉ dưỡng Peppercorn Beach Resort là một không gian yên bình tĩnh lặng, thật sự không quá sang trọng đo điều kiện vật chất trên đảo còn hạn chế, nhưng hương vị mà khu nghỉ dưỡng 3 sao Phú Quốc này mang lại thật hoàn hảo.\r\nKhu nghỉ dưỡng Peppercorn Beach Resort Phú Quốc có một nhà hàng và bar với các món ăn truyền thống của ngư dân trên đảo, cũng như một số các món ăn quốc tế. Và một thực đơn đặc biệt được cung cấp cho du khách ăn trong ngày bao gồm sò điệp nướng, mực nước với salad tất cả nguyên lieu đều được nhà hàng mua từ những ngư dân địa phương trên đảo, đánh bắt hàng ngày. Nhà hàng không sang trọng nhưng có một không gian thoáng đãng bên cạnh bãi biển, cảm nhận làn gió biển mỗi khi thưởng thức các món ăn.', '', '05:00', '18:00', '0', '0', 'peppercornbeach.com', '+84 77 3989 ', 0, 535, '1', 1, 181, NULL, NULL, NULL, '2018-03-30 20:00:00', '2018-03-30 20:00:00');
