delimiter ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `get_idServicePointMax_city`(IN `id_city` INT(10))
    NO SQL
SELECT
	c.id_service 
FROM
	c_city_district_ward_place_service AS c 
WHERE
	c.id_city = id_city 
ORDER BY
	( c.sv_counter_point ) DESC 
	LIMIT 1
;;
