    NO SQL
SELECT
	* 
FROM
	`c_city_district_ward_place_service` AS c 
WHERE
	c.id_city =  id_city
	AND c.id_service <> id_service 
ORDER BY
	c.sv_counter_point DESC
   LIMIT limit_sv
;;
